def define_model(opt):    model_name = opt.model    if model_name == 'Recurrent_T':        from model.RADN import Recurrent_T as M        m = M(rru_nums=6)        return m    elif model_name == 'Recurrent_S':        from model.RADN import Recurrent_S as M        m = M(rru_nums=6)        return m    else:        raise NotImplementedError('Model [{:s}] is not defined.'.format(model_name))    m = M()    print('Training model [{:s}] is created.'.format(m.__class__.__name__))    return m